import { useEffect, useState } from "react";
import "./activities.scss";
import httpModule from "../../helpers/http.module";
import { IActivity } from "../../types/global.typing";
import { Button, CircularProgress, IconButton } from "@mui/material";
import { Add, Edit, Delete } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import ActivitiesGrid from "../../components/activities/ActivitiesGrid.component";

const Activities = () => {
  const [activities, setActivities] = useState<IActivity[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const redirect = useNavigate();

  useEffect(() => {
    setLoading(true);
    httpModule
      .get<IActivity[]>("/Activity/Get")
      .then((response) => {
        setActivities(response.data);
        setLoading(false);
      })
      .catch((error) => {
        alert("Error");
        console.log(error);
        setLoading(false);
      });
  }, []);

  console.log(activities);

  const handleDelete = (id: string) => {
    redirect(`/activities/delete/${id}`);
  };

  const handleUpdate = (id: string) => {
    redirect(`/activities/update/${id}`);
  };

  return (
    <div className="content activities">
      <div className="heading">
        <h2>Activities</h2>
        <Button variant="outlined" onClick={() => redirect("/activities/add")}>
          <Add />
        </Button>
      </div>
      {loading ? (
        <CircularProgress size={100} />
      ) : activities.length === 0 ? (
        <h1>No Activity</h1>
      ) : (
        <>
          <ActivitiesGrid data={activities} />
          {activities.map((activity) => (
            <div key={activity.id}>
              <IconButton color="primary" onClick={() => handleUpdate(activity.id)}>
                <Edit />
              </IconButton>
              <IconButton color="secondary" onClick={() => handleDelete(activity.id)}>
                <Delete />
              </IconButton>
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default Activities;
