import * as React from 'react';
import { BarChart } from '@mui/x-charts';
import { axisClasses } from '@mui/x-charts';
import { IActivity } from "../../types/global.typing";

interface IMyBarsProps {
  data: IActivity[];
}

const MyBars: React.FC<IMyBarsProps> = ({ data }) => {
  const getStatusCounts = () => {
    const counts = {
      New: 0,
      InProgress: 0,
      Done: 0,
      NotDoingIt: 0,
    };

    data.forEach((activity) => {
      switch (activity.status) {
        case 'new':
          counts.New++;
          break;
        case 'inprogress':
          counts.InProgress++;
          break;
        case 'done':
          counts.Done++;
          break;
        case 'notdoingit':
          counts.NotDoingIt++;
          break;
        default:
          break;
      }
    });

    return counts;
  };

  const counts = getStatusCounts();

  const chartSetting = {
    yAxis: [
      {
        label: 'Number of Activities',
      },
    ],
    width: 500,
    height: 300,
    sx: {
      [`.${axisClasses.left} .${axisClasses.label}`]: {
        transform: 'translate(-20px, 0)',
      },
    },
  };

  const dataset = [
    {
      status: 'Activities',
      New: counts.New,
      InProgress: counts.InProgress,
      Done: counts.Done,
      NotDoingIt: counts.NotDoingIt,
    },
  ];

  const valueFormatter = (value: number | null) => `${value}`;

  return (
    <BarChart
      data={dataset}
      xAxis={[{ scaleType: 'band', dataKey: 'status' }]}
      series={[
        { dataKey: 'New', label: 'New', valueFormatter },
        { dataKey: 'InProgress', label: 'InProgress', valueFormatter },
        { dataKey: 'Done', label: 'Done', valueFormatter },
        { dataKey: 'NotDoingIt', label: 'NotDoingIt', valueFormatter },
      ]}
      {...chartSetting}
    />
  );
};

export default MyBars;
