import { useState, useEffect } from "react";
import "./activities.scss";
import { ICreateActivityDto, IActivity } from "../../types/global.typing";
import {
  FormControl,
  TextField,
  InputLabel,
  Select,
  MenuItem,
  Button,
  IconButton,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import httpModule from "../../helpers/http.module";
import SaveIcon from '@mui/icons-material/Save';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import BackIcon from '@mui/icons-material/ArrowBack';
import { useParams } from "react-router-dom";

const AddActivity = () => {
  const [activity, setActivity] = useState<ICreateActivityDto>({
    name: "",
    description: "",
    activityType: "",
    status: "",
    statusTheme: "",
    tag: "",
    tagTheme: "",
  });
  const { id } = useParams();
  const [updateId, setUpdateId] = useState<string | null>(id || null);
  const redirect = useNavigate();

  useEffect(() => {
    if (updateId) {
      fetchActivityById(updateId);
    }
  }, [updateId]);

  const fetchActivityById = async (id: string) => {
    const response = await httpModule.get<IActivity>(`/Activity/Get/${id}`);
    setActivity(response.data);
  };

  const handleClickSaveBtn = () => {
    if (
      activity.name === "" ||
      activity.description === "" ||
      activity.activityType === "" ||
      activity.status === "" ||
      activity.statusTheme === "" ||
      activity.tag === "" ||
      activity.tagTheme === ""
    ) {
      alert("Fill all fields");
      return;
    }

    if (updateId) {
      httpModule
        .put(`/Activity/Update/${updateId}`, activity)
        .then((response) => redirect("/activities"))
        .catch((error) => console.log(error));
    } else {
      httpModule
        .post("/Activity/Create", activity)
        .then((response) => redirect("/activities"))
        .catch((error) => console.log(error));
    }
  };

  const handleClickDeleteBtn = () => {
    if (!updateId) {
      return;
    }

    httpModule
      .delete(`/Activity/Delete/${updateId}`)
      .then((response) => redirect("/activities"))
      .catch((error) => console.log(error));
  };

  const handleClickBackBtn = () => {
    redirect("/activities");
  };

  return (
    <div className="content">
      <div className="add-activity">
        <h2>{updateId ? 'Edit Activity' : 'Add New Activity'}</h2>
        <TextField
          autoComplete="off"
          label="Activity Name"
          variant="outlined"
          value={activity.name}
          onChange={(e) => setActivity({ ...activity, name: e.target.value })}
        />
        <TextField
          autoComplete="off"
          label="Activity Description"
          variant="outlined"
          value={activity.description}
          onChange={(e) =>
            setActivity({ ...activity, description: e.target.value })
          }
        />
        <FormControl fullWidth>
          <InputLabel>Activity Type</InputLabel>
          <Select
            value={activity.activityType}
            label="Activity Type"
            onChange={(e) =>
              setActivity({ ...activity, activityType: e.target.value })
            }
          >
            <MenuItem value="Hobby">Hobby</MenuItem>
            <MenuItem value="School">School</MenuItem>
            <MenuItem value="Job">Job</MenuItem>
            <MenuItem value="Home">Home</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Status</InputLabel>
          <Select
            value={activity.status}
            label="Status"
            onChange={(e) =>
              setActivity({ ...activity, status: e.target.value })
            }
          >
            <MenuItem value="New">New</MenuItem>
            <MenuItem value="InProgress">In Progress</MenuItem>
            <MenuItem value="Done">Done</MenuItem>
            <MenuItem value="NotDoingIt">Not Doing It</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Status Theme</InputLabel>
          <Select
            value={activity.statusTheme}
            label="Status Theme"
            onChange={(e) =>
              setActivity({ ...activity, statusTheme: e.target.value })
            }
          >
            <MenuItem value="blue">Blue</MenuItem>
            <MenuItem value="yellow">Yellow</MenuItem>
            <MenuItem value="pink">Pink</MenuItem>
            <MenuItem value="red">Red</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Tag</InputLabel>
          <Select
            value={activity.tag}
            label="Tag"
            onChange={(e) => setActivity({ ...activity, tag: e.target.value })}
          >
            <MenuItem value="sport">Sport</MenuItem>
            <MenuItem value="course">Course</MenuItem>
            <MenuItem value="responsibilities">Responsibilities</MenuItem>
            <MenuItem value="fun">Fun</MenuItem>
          </Select>
        </FormControl>

        <FormControl fullWidth>
          <InputLabel>Tag Theme</InputLabel>
          <Select
            value={activity.tagTheme}
            label="Tag Theme"
            onChange={(e) =>
              setActivity({ ...activity, tagTheme: e.target.value })
            }
          >
            <MenuItem value="blue">Blue</MenuItem>
            <MenuItem value="yellow">Yellow</MenuItem>
            <MenuItem value="pink">Pink</MenuItem>
            <MenuItem value="red">Red</MenuItem>
          </Select>
        </FormControl>

        <div className="btns">
          <Button
            variant="outlined"
            color="primary"
            startIcon={<SaveIcon />}
            onClick={handleClickSaveBtn}
          >
            Save
          </Button>
          {updateId && (
            <>
              <IconButton
                color="secondary"
                onClick={handleClickDeleteBtn}
              >
                <DeleteIcon />
              </IconButton>
              <IconButton
                color="default"
                onClick={handleClickBackBtn}
              >
                <BackIcon />
              </IconButton>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AddActivity;
