import { useContext, useState } from "react";
import "./navbar.scss";
import { Link } from "react-router-dom";
import { Menu, LightMode, DarkMode } from "@mui/icons-material";
import { ToggleButton } from "@mui/material";
import { ThemeContext } from "../../context/theme.context";

const links = [
   { href: "/", label: "Home" },
   { href: "/activities", label: "Activities" },
   { href: "/mytasks", label: "Mytasks" },
   { href: "/statistics", label: "Statistics" },
];

const Navbar = () => {
   const [open, setOpen] = useState<boolean>(false);
   const { darkMode, toggleDarkMode } = useContext(ThemeContext);

   const ToggleOpenMenu = () => {
      setOpen((prevState) => !prevState);
   };

   const menuStyles = open ? "menu open" : "menu";

   return (
      <div className="navbar">
         <div className="brand">
            <span>Activieties Manager</span>
         </div>
         <div className={menuStyles}>
            <ul>
               {links.map((item) => (
                  <li key={item.href} onClick={ToggleOpenMenu}>
                     <Link to={item.href}>{item.label}</Link>
                  </li>
               ))}
            </ul>
         </div>
         <div className="hamburger">
            <Menu onClick={ToggleOpenMenu} />
         </div>
         <div className="toggle">
            <ToggleButton value={"check"} selected={darkMode} onChange={toggleDarkMode}>
               {darkMode ? <LightMode /> : <DarkMode />}
            </ToggleButton>
         </div>
      </div>
   );
};

export default Navbar;
