//import './statistics.scss'
import { useEffect, useState } from "react";
import "../activities/activities.scss";
import httpModule from "../../helpers/http.module";
import { IActivity } from "../../types/global.typing";
import { Button, CircularProgress } from "@mui/material";
import { Add } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import ActivitiesGrid from "../../components/activities/ActivitiesGrid.component";
import BasicBars from "../../components/statistics/StatisticsBarChart.component";

const Statistics = () => {
  const [activities, setActivities] = useState<IActivity[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const redirect = useNavigate();

  useEffect(() => {
    setLoading(true);
    httpModule
      .get<IActivity[]>("http://localhost:5004/api/Activity/Get")
      .then((response) => {
        setActivities(response.data);
        setLoading(false);
      })
      .catch((error) => {
        alert("Error fetching activities");
        console.log(error);
        setLoading(false);
      });
  }, []);

  interface IActivitiesGridProps {
    data: IActivity[];
  }


  return (
    <div className="content statistics">
      <div className="heading">
        <h2>Statistics of Activities and Tasks</h2>
      </div>
      {loading ? (
        <CircularProgress size={100} />
      ) : activities.length === 0 ? (
        <h1>No Activity</h1>
      ) : (
        <BasicBars 
          data={activities} 
          
        />
      )
    }
    </div>
  );
};

export default Statistics
