import { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';

export default function StatisticsBarChart() {
  const [data, setData] = useState<any>(null);
  const [isLoading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    fetch('http://localhost:5004/api/Activity/Get')
      .then((res) => res.json())
      .then((responseData) => {
        setData(responseData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, []);

  if (isLoading) return <p>Loading...</p>;
  if (!data) {
    console.log("No profile data", data);
    return <p>No profile data</p>;
  }

  const chartData = {
    labels: ['New', 'In Progress', 'Done', 'Not Doing'],
    datasets: [
      {
        label: 'Count',
        data: [
          data.status.new.count,
          data.status.inprogress.count,
          data.status.done.count,
          data.status.notdoing.count,
        ],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(255, 206, 86, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(255, 206, 86, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
        title: {
          display: true,
          text: 'Status',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Count',
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div style={{ width: '100%', maxWidth: '1200px', margin: 'auto', minHeight: '400px' }}>
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
}
