import React, { useState, useEffect } from 'react';
import StatisticsBarChart from '../../components/statistics/StatisticsBarChart.component';

import { IActivity } from '../../types/global.typing';

const Statistics = () => {
  const [activities, setActivities] = useState<IActivity[]>([]);
  const [isLoading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    fetch('http://localhost:5004/api/Activity/Get')
      .then((res) => res.json())
      .then((responseData) => {
        setActivities(responseData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, []);

  if (isLoading) return <p>Loading...</p>;

  return (
    <div className='content statistics'> 
      <h3>See Your Progress</h3>
      <br />
      <br />
      <span>
          This is my page to help you with your activities and tasks.
      </span>
      <div>
        <StatisticsBarChart data={activities} />
      </div>
    </div>
  );
};

export default Statistics;
