import * as React from 'react';
import { ChartContainer, BarPlot, ChartsXAxis, ChartsYAxis, ChartsTooltip, ChartsLegend } from '@mui/x-charts';
import { IActivity } from '../../types/global.typing';

const Statistics = () => {
  const [activities, setActivities] = React.useState<IActivity[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<any>(null); // Changed null to any for error state

  React.useEffect(() => {
    const fetchActivities = async () => {
      try {
        const response = await fetch('http://localhost:5004/api/Activity/Get'); 
        const data = await response.json();
        setActivities(data);
        setLoading(false);
      } catch (err) {
        setError(err);
        setLoading(false);
      }
    };

    fetchActivities();
  }, []);

  const groupActivitiesByStatus = (activities: IActivity[]) => {
    const statusCounts: { [key: string]: number } = {
      New: 0,
      InProgress: 0,
      Done: 0,
      NotDoingIt: 0,
    };

    activities.forEach((activity) => {
      const status = activity.status.charAt(0).toUpperCase() + activity.status.slice(1); // Capitalize the status
      if (statusCounts.hasOwnProperty(status)) {
        statusCounts[status]++;
      }
    });

    return statusCounts;
  };

  const statusCounts = groupActivitiesByStatus(activities);

  const chartData = [
    { status: 'New', count: statusCounts.New },
    { status: 'InProgress', count: statusCounts.InProgress },
    { status: 'Done', count: statusCounts.Done },
    { status: 'NotDoingIt', count: statusCounts.NotDoingIt },
  ];

  const xLabels = chartData.map((item) => item.status);
  const uData = chartData.map((item) => item.count);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error fetching data</p>;

  return (
    <div className='content statistics'> 
      <h3>Your situation</h3>
      <br />
      <br />
      <span>
          Be proud of your progress.
      </span>
      <br />
      <br />
      <ChartContainer width={500} height={300}>
        <BarPlot 
          data={uData} 
          label="Count" 
          id="countId" 
        />
        <ChartsXAxis 
          data={xLabels} 
          scaleType="band" 
          categoryGapRatio={0.3} 
          barGapRatio={0.1} 
        />
        <ChartsYAxis />
        <ChartsTooltip />
        <ChartsLegend />
      </ChartContainer>
    </div>
  );
};

export default Statistics;
