import { useState, useEffect } from "react";
import "./activities.scss";
import { IActivity } from "../../types/global.typing";
import {
  FormControl,
  TextField,
  InputLabel,
  Select,
  MenuItem,
  Button,
} from "@mui/material";
import { useNavigate, useParams } from "react-router-dom";
import httpModule from "../../helpers/http.module";

const UpdateActivity = () => {
  const [activity, setActivity] = useState<IActivity>({
    id: "",
    name: "",
    description: "",
    activityType: "",
    status: "",
    statusTheme: "",
    tag: "",
    tagTheme: "",
    createdAt: "",
    updatedAt: "",
  });

  const { id } = useParams<{ id?: string }>();
  const redirect = useNavigate();

  useEffect(() => {
    if (id) {
      fetchActivityById(id);
    }
  }, [id]);

  const fetchActivityById = async (id: string) => {
    try {
      const response = await httpModule.get<IActivity>(`/Activity/Get/${id}`);
      setActivity(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setActivity({
      ...activity,
      [name]: value,
    });
  };

  const handleClickSaveBtn = () => {
    httpModule
      .put(`/Activity/Update/${id}`, activity)
      .then((response) => redirect("/activities"))
      .catch((error) => console.log(error));
  };

  const handleClickBackBtn = () => {
    redirect("/activities");
  };

  return (
    <div className="content">
      <div className="add-activity">
        <h2>Edit Activity</h2>
        <FormControl fullWidth>
          <TextField
            name="name"
            label="Activity Name"
            value={activity.name}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
          />
          <TextField
            name="description"
            label="Description"
            value={activity.description}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            multiline
            rows={4}
          />
          {/* Add other input fields as per your requirements */}
        </FormControl>
        <div className="btns">
          <Button
            variant="outlined"
            color="primary"
            onClick={handleClickSaveBtn}
          >
            Save
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            onClick={handleClickBackBtn}
          >
            Back
          </Button>
        </div>
      </div>
    </div>
  );
};

export default UpdateActivity;
